import React from 'react'
import Avatar from '@material-ui/core/Avatar'
const Message = (props) => {
    let messageclass=""
    let day;
    if(props.by==="me")
    {
        messageclass="msg-left"
    }
    else if (props.by==="day")
    {
       day=<li class="msg-day"><small>{props.children}</small></li>
    }
    else{
        messageclass="msg=right"
    }
    let chat =<li className={messageclass}>
    <div className="msg-left-sub">
        <Avatar/>
        <div className="msg-desc">
            Lorem  tempor incididunt ut labore et dolore magna aliqua.
        </div>
        <small>05:25 am</small>
    </div>
</li>;

let printing;

if (props.by==="day")
{
printing=day
}
else{
    printing=chat
}
    return (
        <div>
             {printing}
        </div>
    )
}

export default Message;
