import React, {Component} from 'react';
import './App.css';
import HeadSection from './head/head'
import Body from './body/body'

class App extends Component {
  state={
    darktoggle:false
  }

  darkModeHandler=()=>
{
  let darkMode= this.state.darktoggle
  this.setState({
    darktoggle:!darkMode
  })
}


  render() {
    return (
      <div className="main-section">
        <button onClick={this.darkModeHandler}></button>
       <HeadSection  name="Yogesh Bhattarai" status="online" dark={this.state.darktoggle}/>
        <Body />
      </div>
    );
  }
}

export default App;
