import React from 'react'
import Avatar from '@material-ui/core/Avatar'
const Message = (props) => {
    let messageclass=""
    let chat =<li className={messageclass}>
    <div className="msg-left-sub">
        <img src="#"/>
        <div className="msg-desc">
            Lorem  tempor incididunt ut labore et dolore magna aliqua.
        </div>
        <small>05:25 am</small>
    </div>
</li>;
    if(props.by==="me")
    {
        messageclass="msg=left"
    }
    else if (props.by==="day")
    {
        chat=<li class="msg-day"><small>{props.day}</small></li>
    }
    else{
        messageclass="msg=right"
    }
    return (
        
             {chat}
        
    )
}

export default Message;
